package com.google.test;

public class OperatorDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		System.out.println(10+20);
		System.out.println(10+"20");
		System.out.println("10"+"20");
		System.out.println("10"+20);
	
		int a = 5;
		//pre increment
		System.out.println(++a);
		
		int b=8;
		//post increment
		System.out.println(b++);		
		int c=b;
		System.out.println(c);
	}

}
